string = "\u062f\u0627\u0646\u0644\u0648\u062f \u0641\u06cc\u0644\u0645 Furlough 2018. \u062f\u0631 \u062e\u0644\u0627\u0635\u0647 \u062f\u0627\u0633\u062a\u0627\u0646 \u0627\u06cc\u0646 \u0641\u06cc\u0644\u0645 \u06a9\u0645\u062f\u06cc \u0648 \u0645\u0627\u062c\u0631\u0627\u06cc\u06cc \u0622\u0645\u062f\u0647 \u0627\u0633\u062a \u060c \u0647\u0646\u06af\u0627\u0645\u06cc \u06a9\u0647 \u062a\u0635\u0645\u06cc\u0645 \u06af\u0631\u0641\u062a\u0647 \u0645\u06cc\u200e\u0634\u0648\u062f \u06cc\u06a9 \u0632\u0646\u062f\u0627\u0646\u06cc \u0628\u0631\u0627\u06cc \u062f\u06cc\u062f\u0646 \u0645\u0627\u062f\u0631 \u062f\u0631 \u062d\u0627\u0644 \u0645\u0631\u06af\u0634 \u0628\u0647 \u0645\u062f\u062a \u06a9\u0648\u062a\u0627\u0647\u06cc \u0627\u0632 \u0632\u0646\u062f\u0627\u0646 \u0622\u0632\u0627\u062f \u0634\u0648\u062f \u060c \u06cc\u06a9 \u067e\u0644\u06cc\u0633\u200e \u062a\u0627\u0632\u0647\u200e\u06a9\u0627\u0631 \u0645\u0633\u0648\u0648\u0644 \u06a9\u0646\u062a\u0631\u0644 \u0627\u06cc\u0646 \u0632\u0646\u062f\u0627\u0646\u06cc \u0645\u06cc\u200e\u0634\u0648\u062f. \u0627\u0645\u0627\u2026"
